// 389. Find the Difference

// You are given two strings s and t.
// String t is generated by random shuffling string s and then add one more letter at a random position.
// Return the letter that was added to t.

// Example 1:
// Input: s = "abcd", t = "abcde"
// Output: "e"
// Explanation: 'e' is the letter that was added.

// Example 2:
// Input: s = "", t = "y"
// Output: "y"

// Example 3:
// Input: s = "a", t = "aa"
// Output: "a"

// Example 4:
// Input: s = "ae", t = "aea"
// Output: "a"

// Using arr split
/**
 * @param {string} s
 * @param {string} t
 * @return {character}
 */
var findTheDifference = function(s, t) {
    const sArr = s.split('');
    let tArr = t.split('');
    while(sArr.length>0) {
        const index = tArr.indexOf(sArr.pop());
        tArr = [...tArr.slice(0,index), ...tArr.slice(index+1)];
    }
    return tArr[0];
};

// Using obj
/**
 * @param {string} s
 * @param {string} t
 * @return {character}
 */
var findTheDifference = function(s, t) {
    let sObj = {};
    for(let c of s) sObj[c] = sObj[c] ? ++sObj[c] : 1;
    for(let d of t) {
        if(sObj[d]) sObj[d]--;
        else return d;
        if(sObj[d]<=0) delete sObj[d];
    }
    return '';
};
